import Root from '__GENERATED__/root.svelte';
// @ts-expect-error - doesn't exist yet. generated by Rollup
import { routes, fallback } from '__GENERATED__/manifest.js';
import { Router } from './router.js';
import { Renderer } from './renderer.js';
import { init } from './singletons.js';
import { set_paths } from '../paths.js';

/**
 * @param {{
 *   paths: {
 *     assets: string;
 *     base: string;
 *   },
 *   target: Node;
 *   session: any;
 *   route: boolean;
 *   spa: boolean;
 *   trailing_slash: import('types').TrailingSlash;
 *   hydrate: {
 *     status: number;
 *     error: Error;
 *     nodes: Array<Promise<import('types').CSRComponent>>;
 *     params: Record<string, string>;
 *   };
 * }} opts
 */
export async function start({ paths, target, session, route, spa, trailing_slash, hydrate }) {
	const renderer = new Renderer({
		Root,
		fallback,
		target,
		session
	});

	const router = route
		? new Router({
				base: paths.base,
				routes,
				trailing_slash,
				navigation_handler: renderer.handle_navigation.bind(renderer)
		  })
		: null;
	renderer.router = router;

	init({ router, renderer });
	set_paths(paths);

	if (hydrate) await renderer.hydrate(hydrate);
	if (router) {
		if (spa) router.goto(location.href, { replaceState: true });
		router.init_listeners();
	}
	renderer.init_listeners();

	dispatchEvent(new CustomEvent('sveltekit:start'));
}
